import React, { useEffect, useState } from 'react';
import style from './ConstructorString.module.css'
import NewString from './NewString';


function ConstructorString({ setNewtring }) {
  const [text, setText] = useState('')
  const [subtext, setSubtext] = useState('')
  const [visible, setVisible] = useState(false)

  const addText = () => {
    setVisible(true)
  }

  const newstring = {
    content: text,
    subcontent: subtext
  }
  useEffect(() => {
    setNewtring((prev) => [newstring])
  }, [text, subtext])

  return (
    <div className={style.contaner}>
      {/* Текст
      <div>
        <input onChange={(e) => setText(e.target.value)} placeholder='Содержание строки' />
      </div>
      Подстрока
      <div>
        <input onChange={(e) => setSubtext(e.target.value)} placeholder='Содержание подстроки' />
      </div> */}
      <NewString setText={setText} setSubtext={setSubtext} />
      <div>
        {visible && <NewString setText={setText} setSubtext={setSubtext} />}
      </div>
      <button onClick={addText} className={style.button}>+Добавить подстроку</button>

    </div>
  );
}

export default ConstructorString;
